SELECT T1.REFERENCE_ID as reference_id,
  T1.URL as about,
  T1.Primary_Text as text
FROM RIDW.VW_REQUIREMENT T1
LEFT OUTER JOIN RICALM.VW_RQRMENT_ENUMERATION T2
ON T2.REQUIREMENT_ID=T1.REQUIREMENT_ID AND T2.NAME='Release'
WHERE T1.PROJECT_ID = 19  AND
(  T1.REQUIREMENT_TYPE NOT IN ( 'Test Dependency','Formal Use Case (Text)','Technical Design Requirement','Change Request','Actor','Use Case','Subtopic','Formal Use Case (Text)','User Story','Spec Proxy','Function Point','Process Inventory','Term','Use Case Diagram' ) AND
  T2.LITERAL_NAME IN ('Commercial ' || chr(38) || ' Construction Lending','Two DOT One','Two DOT Five','Two DOT Nine')  AND
  (SUBSTR(T1.URL,43,3) = 'CA_' OR LENGTH(T1.URL) = 65) AND
  TRUNC(T1.REC_DATETIME) <= TO_DATE(:endDate,'mm/dd/yyyy')
) AND
(T1.STATUS not in ('Delete','Rejected','Deprecated','Duplicate') OR T1.STATUS IS NULL) AND
T1.ISSOFTDELETED = 0 AND
t1.reference_id in ('592272','592273','592274','592275','592276','592277','592278','592279','592280','592281','592282','592283','592284','592285','592286','592287','592288','592289','592290','592291','592298','592299','592300','592301','592302','592303','592304','592305','592306','592307','592308','592309','592310','592311','592312','592313','592314','592315','592316','592317','592320','592321','592322','592323','592324','592325','592326','592327','592328','592329','592330','592331','592332','592333','592334','592335','592336','592337','592338','592339','592340','592341','592342','592343','592344','592345','592346','592350','592351','592352','592353','592354','592355','592356','592357','592358','592359','592360','592361','592362','592363','592364','592365','592366','592367','592368','592369','592370','592371','592372','592373','592374','592375','592376','592377','592378','592379','592380','592381','592382','592383','592384','592385')
and (T1.REQUIREMENT_ID <> -1 AND T1.REQUIREMENT_ID IS NOT NULL)
ORDER BY T1.REFERENCE_ID