        "Custom.AffiliateFoundIn": "ZFNB",
        "WEF_FDD1A76DF2614CFBB2897BE9FA17D038_System.ExtensionMarker": true,
        "WEF_FDD1A76DF2614CFBB2897BE9FA17D038_Kanban.Column": "New",
